<?phprequire_once 'AbstractView.class.php';require_once 'Secciones.php';class TabsAdminView extends AbstractView {		var $partes;	/**	 * Constructor	 * @return unknown_type	 */	public function TabsAdminView(){		$this->partes = array();	}	public function setDisplayOptions($tab){		switch($tab){			case "aprobarPartes":				$this->template = "adminAprobar.html";								$partes = DB_DataObject::factory("partes");				$partes->par_aprobado = 0;				$partes->find();								while( $partes->fetch() ){					$this->partes[$partes->par_id] = clone ($partes);				}				break;							case "editarPartes":				$this->template = "adminEditar.html";								break;			case "editarDocentes":				$this->template = "adminEditarDocentes.html";				break;			case "informes":				$this->template = "adminInformes.html";				break;			// aca empieza el sub-tab de los tabs			case "partesIngresados":				$this->template = "adminPartesDetail.html";				break;							case "ayuda":				$this->template  = "ayudaAdmin.html";				break;								case "premiosAsistencia":				$this->template = "premiosAsistencia.html";				break;							default:				break;		}	}	public function getDBproperty($p_object, $p_property){				$p_object->getLinks();		$methods = explode(".",$p_property);		$value = $p_object;		foreach($methods as $method){			$value = $value->$method;		}				return utf8_encode($value);	}}?>