<?phprequire_once 'AbstractView.class.php';require_once 'Secciones.php';class TabsReportesView extends AbstractView {		var $partes;        var $sec_id;	/**	 * Constructor	 * @return unknown_type	 */	public function TabsReportesView(){		$this->partes = array();	}	public function setDisplayOptions($tab){		switch($tab){			case "notificaciones":                            $this->template  = "notificacionesUsuario.html";                            break;                        case "reportesIngresados":                            $this->template = "reportesIngresados.html";                            // busco el id de la seccion                            $seccion = DB_DataObject::factory("secciones");                            $seccion->sec_nombre = SessionVars::getSeccion();                            $seccion->find(); $seccion->fetch();                            $this->sec_id = $seccion->sec_id;                            break;                        case "ayuda":                            $this->template = "ayudaUsuario.html";                            break;                        case "cerrarSesion":                            break;			default:				break;		}	}	public function getDBproperty($p_object, $p_property){				$p_object->getLinks();		$methods = explode(".",$p_property);		$value = $p_object;		foreach($methods as $method){			$value = $value->$method;		}				return utf8_encode($value);	}}?>