<div class="contentBody">		<script src="js/jquery-grid/js/grid.locale-es.js" type="text/javascript"></script>	<script src="js/jquery-grid/js/jquery.jqGrid.min.js" type="text/javascript"></script>	<script type="text/javascript" src="js/jqgridTables.js"></script>			<div id="docentes" class="gridTable" style="margin:20px auto">		<table id="docentesTable"></table>		<div id="docentesPager"></div>	</div>		<div id="alumnos" class="gridTable" style="margin:20px auto">		<table id="alumnosTable"></table>		<div id="alumnosPager"></div>	</div>		<script type="text/javascript">				$(document).ready(function(){							// tabla para los docentes				var docColNames = ['Id', 'Nombre', 'Cargo', 'CUIL', 'Horas', 'Situacion de revista', 'Seccion'];				var docColModel = [			   		{name:'doc_id',index:'doc_id'},					{name:'doc_nombre',index:'doc_nombre', editable: true},                                        {name:'doc_cargo', index: 'doc_cargo', editable: true},                                        {name:'doc_cuil', index: 'doc_cuil', editable: true},                                        {name:'doc_hs', index: 'doc_hs', editable: true},                                        {name:'doc_revista', index: 'doc_revista', editable: true},                                        {name:'doc_seccion', index: 'doc_seccion', editable: true, edittype: 'select', editoptions:{value: 'AD:AD; DE:DE; J2:J2; J4:J4; MT:MT; P1:P1; P3:P3; P6:P6; S5:S5; S6:S6; S7:S7; S9:S9' }}				];				var docFieldsCsv = "fieldsCsv=doc_id,doc_nombre,doc_cargo,doc_cuil,doc_hs,doc_revista,doc_seccion";				var docUrl = 'adminReports.php?q=1&action=jqgridTable&table=docente' + "&" + docFieldsCsv ;				createTable("#docentesTable", docUrl, docColNames, docColModel, '#docentesPager', "Docentes", prepareDocentesForm);                                				// tabla para los alumnos				var aluColNames = ['Id', 'Nombre', 'Zona', 'Identificador'];				var aluColModel = [			   		{name:'alu_id',index:'alu_id'},                                        {name:'alu_nombre',index:'alu_nombre', editable: true},					{name:'alu_zona',index:'alu_zona', editable: true},                                        {name:'alu_identificador',index:'alu_identificador', editable: true}                                        				];				var aluFieldsCsv = "fieldsCsv=alu_id,alu_nombre,alu_zona,alu_identificador";				var aluUrl = 'adminReports.php?q=1&action=jqgridTable&table=alumnos' + "&" + aluFieldsCsv ;				createTable("#alumnosTable", aluUrl, aluColNames, aluColModel, '#alumnosPager', "Alumnos", prepareAlumnosForm);													})                function prepareDocentesForm(){                    // populate the 'cargos' select                    $("#doc_cargo").autocomplete({                            minLength: 1,                            source: "adminReports.php?action=listarCargosDocentes",                            focus: function(event, ui){                                    $(this).val(ui.item.label);                                    return false;                            },                            select: function(event, ui){                                    $(this).val(ui.item.label);                                    return false;                                    }                    })		}                function prepareAlumnosForm(id){                    // TODO: implementar esta funcion para prepara el formulario                   $("#alu_zona").autocomplete({                            minLength: 1,                            source: "adminReports.php?action=listarZonasAlumnos",                            focus: function(event, ui){                                    $(this).val(ui.item.label);                                    return false;                            },                            select: function(event, ui){                                    $(this).val(ui.item.label);                                    return false;                                    }                    })                };	</script>	</div>